package pAYILAGAM;

public class DesendingOrder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0;i<=0;i++)
		{
			for(int j=1;j>i;j--)
			{
				System.out.print("4\n");
				System.out.println("33");
			}
			
			for(int k=1;k>i;k--)
			{
				System.out.print("222\n");
				System.out.println("1111");
			
			}System.out.println();
			
		}

	}

}
