package pAYILAGAM;

public abstract class FOURtoONE {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0;i<1;i++)
		{
		for(int j=4;j>i;j--)
		{
			System.out.print("4");
		}
		System.out.println();
		for(int k=3;k>i;k--)
		{
			System.out.print("3");
		
		}
		System.out.println();
		for(int l=2;l>i;l--)
		{
			System.out.print("2");
			
		
		}
		System.out.println();
		for(int m=1;m>i;m--)
		{
			System.out.print("1");
		}
		System.out.println();
		}

	}

}
