package pAYILAGAM;

public class WhileprogramusingFOR {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 0;
		while(a<=5){

			int b= 5;
			while(b>0){
				System.out.print(b);
				b--;
				}
			System.out.println("   a is "+a);
		  a++;
		}
		

	}

}
